// Contains the default vector bindings used in program taut_enum
// for both standadising and enumerating tautomers.

#ifndef TAUT_ENUM_DEFAULT_VECTOR_BINDINGS_H
#define TAUT_ENUM_DEFAULT_VECTOR_BINDINGS_H

#include <string>

namespace DACLIB {

const std::string VBS = "\n\
#\n\
#  sbde_standardise.vb_1.2                         PW Kenny, 15-Aug-2005\n\
#\n\
#  SBDE Structure standardisation vector bindings    Version 1.2\n\
#\n\
#  These SMARTS were generated by making sbde_standardise.vb_v1.1\n\
#  SMARTS compatible with both Daylight and OEChem which differ in \n\
#  their use of the R and r descriptors.  \n\
#\n\
#  The SMARTS definitions in this file are the property of \n\
#  AstraZeneca and must not be distributed or otherwise made \n\
#  available outside the Company\n\
#\n\
#AGL JULY 2011: extra OS definition removed\n\
########################################################################\n\
#\n\
#  Redefine r & R descriptors to make compatible with OEChem & Daylight \n\
# AGL: Extra ~ added Nov 2011\n\
r3      *1~*~*~1\n\
r4      *1~*~*~*~1\n\
r5      *1~*~*~*~*~1\n\
r6      *1~*~*~*~*~*~1\n\
r7      *1~*~*~*~*~*~*~1\n\
r8      *1~*~*~*~*~*~*~*~1\n\
R       *(~;@*)~;@*\n\
R2      *(~;@*)(~;@*)~;@*\n\
RSpiro  A(~;@*)(~;@*)(~;@*)~;@*\n\
R1      [$R;!$R2;!$RSpiro]\n\
#\n\
#  Define bland metals\n\
BlandMet  [Li,Na,K,Mg,Ca,Al]\n\
#\n\
#  Some atoms types for de-salting and small fragment removal\n\
MeEt    [$([CH3]),$([CH2][CH3])]\n\
#  \n\
#  Some really grotesque structures in Beilstein were giving \n\
#  Leatherface a real working over so the ylid definitions have\n\
#  been made more restrictive. \n\
# \n\
YLID1   [N,n,P,S;+;!H;!H2;!H3][A;-]\n\
YLID2   [N,O,S;-][$YLID1]\n\
#\n\
#  Generalised pi-donor in pyridone etc\n\
#\n\
PiDon   [$([nX3]),$([o,s;X2]);!$(n=A)]\n\
#\n\
#  Hydroxy or thiol\n\
#\n\
#OS      [O,S;!+;H;D1]\n\
#AGL\n\
OS      [O,S;!+;H]\n\
cOS     c[$OS]\n\
#\n\
#  Doubly-connected aromatic nitrogen...note exlusion of nitrogens\n\
#  in structures that normal valency rules prevent from converting\n\
#  to keto tautomers \n\
#\n\
Nexc1  n([$cOS]a1)[a;!$cOS]c2c1a[o,s,n&X3]a2\n\
NPY    [n;D2;!+;!H;!$Nexc1]\n\
NImid  n1anaa1\n\
NPYNoImid [$NPY;!$NImid]\n\
#\n\
#  Hydroxy or thiol\n\
#\n\
# OS      [O,S;!+;H;D1]\n\
#  This is necessary to identify potential carbonyl groups\n\
#  adjacent to carbons which cannot form a Kekule double bond\n\
#  to their other directly bonded partner\n\
CJNK1   c[c;$R1][c;$R2][n;X2][n&X3,o]\n\
#  Potential carbonyl/thiocarbonyl group\n\
CXH     [c;!$CJNK1][$OS]\n\
#\n\
#  For defining aromatic atoms...note addition of CMacyc\n\
#  to prevent upleasant things happening with macrocyclic\n\
#  species such as analogues of porphyrins & phthalocyanins\n\
#\n\
NOX     [n;D3;!+;!H]=[O,S]\n\
CX2     c=A\n\
CX2Ext caac=A\n\
CMacyc  c~[a;!$r5;!$r6]\n\
#CX2B    [c;$CX2,$CX2Ext,$CMacyc]\n\
CX2B    [c;$CX2,$CMacyc]\n\
CAR     [c;!$CX2B]\n\
AR      [$NPY,$NOX,$CAR]\n\
ARFUS   [a;$R2](:a)(:a):a \n\
CARFUS  [c;$R2](:a)(:a):a \n\
#\n\
#  Definitions for orienting benzimidazole/benzotriazole N-H\n\
#\n\
Don     [$(c[N;H,H2]),$([nH])]\n\
Acc     [$([nX2]),$(a=[O,S;X1]),$([c;$R2][nX2])]\n\
NoHB    [a;!$Don;!$Acc]\n\
#\n\
#------------------------------------------------------------------\n\
#\n\
#  Deuterated nitrogen is problematic when trying to orient\n\
#  benzimidazoles/benzotriazoles and simplest solution is\n\
#  to not to do any thing about it because the parent protiated\n\
#  form should also be in database\n\
NtoDeu   [n][2H]\n\
NHnotDeu [n;H;!$NtoDeu;!+] \n\
#\n\
#  These define a 2-pyridone carbonyl or vinylogue\n\
OXO1    [c;$r6](=[O,S])[n;X3;!+]\n\
OXO2    [c;$r6](=[O,S])[$AR][$AR][n;X3;!+]\n\
OXO3    [$AR;$r5][$AR;$r5][$AR;$r5][a;$R2]\n\
OXO     [$OXO1,$OXO2,$OXO3]\n\
#\n\
#--------------------------------------------------------------------\n\
#\n\
#  Neutralisation of ionised groups  \n\
#\n\
#  Ionised groups to be neutralised. Macrocycles can be\n\
#  problematic and it's unlikely that these will be of much interest\n\
#  as drugs so exclude these from definitions of anions.  Deuteriated\n\
#  bases will not deprotonate properly.  These cases will be rare and\n\
#  it is important that deuterium not be lost from structure; treat\n\
#  as a special case an leave alone\n\
#\n\
DEUTCAT [A,a;+][2H]\n\
MACRATM [a;!$r3;!$r4;!$r5;!$r6;!$r7;!$r8]\n\
MACR1   [n;-][$MACRATM]\n\
MACR2   [n;-]a[$MACRATM]\n\
MACR3   [n;-]aa[$MACRATM]\n\
MACR    [$MACR1,$MACR2,$MACR3] \n\
#\n\
#  Note more specific definition of ionised groups to avoid\n\
#  neutralising fluoroborates and the like\n\
#\n\
ANION   [N,O,S,n;-;!$YLID2;!$MACR]\n\
CATION  [n,N;+;H,H2,H3;!$DEUTCAT]\n\
#\n\
#  Disconnected fragments\n\
Disconn ([a,A]).([A,a])\n\
#--------------------------------------------------------------------\n\
# These below added to facilitate tautomer enumeration - they should be the \n\
# groups that those above are generally transformed into\n\
# AGL October 2011\n\
REV_OS [O,S!+;H0]=*\n\
REV_NPY [n;X3;!+;H1;!$Nexc1]\n\
REV_NHnotDeu [n;H0;!+]\n\
EXO_NH [N;!+;!H0]a\n\
EXO_NDB1 [N;!+;H0;X2]=a\n\
EXO_NDB2 [N;!+;H1;X2]=a\n\
EXO_NDB [$EXO_NDB1,$EXO_NDB2]\n\
# It is a problem that Hs are treated as explicit in SMIRKS so count towards the D \n\
# operator.  These are aiming to work around this problem\n\
O_ONE_CONNECTION_H [O;D2;H1]\n\
O_ONE_CONNECTION_OTHER [O;D1]\n\
O_ONE_CONNECTION [$O_ONE_CONNECTION_H,$O_ONE_CONNECTION_OTHER]\n\
O_NO_CONNECTION_H2 [O;D2;H2;X2]\n\
O_NO_CONNECTION_H1 [O;D1;H1;X1]\n\
O_NO_CONNECTION_H0 [O;D0]\n\
O_NO_CONNECTION [$O_NO_CONNECTION_H0,$O_NO_CONNECTION_H1,$O_NO_CONNECTION_H2]\n\
N_THREE_CONNECTION_H1 [N;H1;X4]\n\
N_THREE_CONNECTION_H0 [N;H0;X3]\n\
N_TWO_CONNECTION_H2 [N;H2;X4]\n\
N_TWO_CONNECTION_H1 [N;H1;X3]\n\
N_TWO_CONNECTION_H0 [N;H0;X2]\n\
N_ONE_CONNECTION_H3 [N;H3;X4]\n\
N_ONE_CONNECTION_H2 [N;H2;X3]\n\
N_ONE_CONNECTION_H1 [N;H1;X2]\n\
N_ONE_CONNECTION_H0 [N;H0;X1]\n\
N_NO_CONNECTION_H4 [N;H4;X4]\n\
N_NO_CONNECTION_H3 [N;H3;X3]\n\
N_NO_CONNECTION_H2 [N;H2;X2]\n\
N_NO_CONNECTION_H1 [N;H1;X1]\n\
N_NO_CONNECTION_H0 [N;H0;X0]\n\
N_THREE_CONNECTION [$N_THREE_CONNECTION_H1,$N_THREE_CONNECTION_H0]\n\
N_TWO_CONNECTION [$N_TWO_CONNECTION_H2,$N_TWO_CONNECTION_H1,$N_TWO_CONNECTION_H0]\n\
N_ONE_CONNECTION [$N_ONE_CONNECTION_H3,$N_ONE_CONNECTION_H2,$N_ONE_CONNECTION_H1,$N_ONE_CONNECTION_H0]\n\
N_NO_CONNECTION [$N_NO_CONNECTION_H4,$N_NO_CONNECTION_H3,$N_NO_CONNECTION_H2,$N_NO_CONNECTION_H1,$N_NO_CONNECTION_H0]\n\
C_NO_CONNECTION_H4 [C;X4;H4]\n\
C_NO_CONNECTION_H3 [C;X3;H3]\n\
C_NO_CONNECTION_H2 [C;X2;H2]\n\
C_NO_CONNECTION_H1 [C;X1;H1]\n\
C_NO_CONNECTION_H0 [C;X0;H0]\n\
C_NO_CONNECTION [$C_NO_CONNECTION_H4,$C_NO_CONNECTION_H3,$C_NO_CONNECTION_H2,$C_NO_CONNECTION_H1,$C_NO_CONNECTION_H0]\n\
HALIDE_NO_CONNECTION_H1 [F,Cl,Br,I;X1;H1]\n\
HALIDE_NO_CONNECTION_H0 [F,Cl,Br,I;X0;H0]\n\
HALIDE_NO_CONNECTION [$HALIDE_NO_CONNECTION_H1,$HALIDE_NO_CONNECTION_H0]";

} // end of namespace DACLIB

#endif // TAUT_ENUM_DEFAULT_VECTOR_BINDINGS_H
